const axios = require('axios')

exports.meme = function(userID, token) {
    return new Promise((resolve,rej) => {
        axios
            .get(`https://bunkapi.xyz/api/v1/meme/${userID}?token=${token}`)
            .then(res => {
                resolve(res.data.data);
            })
            .catch(error => {
                rej(error)
            })
    });
}

